import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import TransferList from './TransferList';
import { getAdminCourses, getBannedUsers, getNotBannedUsers } from './utils/fetchs';
import List from './List';
import { Button, FormControlLabel, FormGroup, Switch, TextField } from '@mui/material';
import { Palette } from './utils/colors';

const Admin = () => {
    const [expanded, setExpanded] = useState(false);
    const [banned , setBanned] = useState([])
    const [notBanned, setNotBanned] = useState([])
    const [courses, setCourses] = useState([])
    const [links, setLinks] = useState([])
    const [check, setCheck] = useState(false)

    const handleChangeCheck = (event) => {
      setCheck(event.target.checked);
    }


    const coursesTitle = courses.map(course=> course.title);
    const linksTitle = links.map(link => link.title);

    const handleCourseSelected = (title, currentIndex) => {
      if(currentIndex !== 0){
        let links = courses.filter(course => course.title === title)
        setLinks([...links])
      }else{
        setLinks([])
      }

    }

    const handleLinkSelected = (link) => {

    }

    const handleChange = (panel) => (event, isExpanded) => {
      setExpanded(isExpanded ? panel : false);
    };

    useEffect(()=>{
      const getInfo = async() => {
        let response = await getBannedUsers()
        setBanned([...response.map(user => user.username)])
        response = await getNotBannedUsers()
        setNotBanned([...response.map(user => user.username)])
        response = await getAdminCourses()
        setCourses([...response])
      }
      getInfo();
    }, [])

    return (
      <Body>
        <Accordion expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
          <AccordionSummary
            aria-controls="panel1bh-content"
            id="panel1bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>
              Users
            </Typography>
            <Typography sx={{ color: 'text.secondary' }}>Manage all the users</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <TransferList rightData={banned} leftData={notBanned}/>
          </AccordionDetails>
        </Accordion>


        <Accordion expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
          <AccordionSummary
            aria-controls="panel2bh-content"
            id="panel2bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>Courses</Typography>
            <Typography sx={{ color: 'text.secondary' }}>
              Manage all the courses and link availables
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
          <FormGroup>
            <FormControlLabel control={<Switch checked={check} onChange={handleChangeCheck} />} label="Create/Update" />
          </FormGroup>
            <h1>Select a course to update</h1>
            <UpdateSection>
              <List newData={coursesTitle} selected={handleCourseSelected}/>
              <UpdateForm>
                <TextField sx={{margin: '10px 0px',}}label="Title" placeholder='Title of Course'/>
                <TextField sx={{margin: '10px 0px'}}label="Description" placeholder='Description of Course' multiline rows={4}/>
                <Button sx={{backgroundColor: Palette.SECONDARY, margin: '10px 0px', color: 'black'}} >Update course</Button>
              </UpdateForm> 
            </UpdateSection>
            {/* <UpdateSection>
              <List newData={linksTitle} selected={handleLinkSelected}/>
              <TextField placeholder='here goes the title of the course'/>
              <Button>Update course</Button>
            </UpdateSection> */}
          </AccordionDetails>
        </Accordion>

      </Body>
    );
  }

export default Admin;

const Body = styled.div`
    height: 500px;
`

const UpdateSection = styled.div`
    display: flex;
`

const UpdateForm = styled.div`
    display: flex;
    flex-direction: column;
    height: 300px;
`
