import React from 'react';
import styled from 'styled-components';
import { Button, TextField } from '@mui/material';
import { Palette } from './utils/colors';
import { useDispatch } from 'react-redux';
import { logIn, signIn, none } from './utils/stores/log';
import { useSelector } from 'react-redux';


const LogIn = () => {
    const log = useSelector(state => state.log.value)
    const dispatch = useDispatch();
    const changeLogState = (newValue) => {
        switch(newValue){
            case 'none':
                dispatch(none())
                break;
            case 'signin':
                dispatch(signIn())
                break;
            case 'login':
                dispatch(logIn())
                break;
        }
    }

    const dependsOnLogState = () => {
        if(log === 'login'){
            return (
                <>
                    <Button sx={{backgroundColor: Palette.SECONDARY}}variant="contained"> Log In</Button>
                    <Line/>
                    <p>Dont have an account? <mark onClick={() => changeLogState('signin')}>Sign In</mark></p> 
                </>
            )
        }else{
            return (
                <>
                    <TextField required id="filled-basic" label="Email" variant="filled"/>
                    <TextField required sx={{margin: '10px 0px', width: '75%'}} id="filled-basic" label="Name" variant="filled"/>
                    <TextField required id="filled-basic" label="Last Name" variant="filled"/>
                    <Button sx={{backgroundColor: Palette.SECONDARY, margin: '10px 0px'}}variant="contained"> Sign In</Button>

                    <Line/>
                    <p>Already have an account? <mark onClick={() => changeLogState('login')}>Log In</mark></p> 
                </>
            )
        }
    }

    return ( 
        <Auth>
            <AuthForm>
                <h1>Welcome Trader</h1>
                <TextField required type='text' id="filled-basic" label="Username" variant="filled"/>
                <TextField required sx={{margin: '10px 0px'}} type='password' id="filled-basic" label="Password" variant="filled"/>
                {dependsOnLogState()}

            </AuthForm>   
        </Auth>
    );
}

export default LogIn;

const Auth = styled.div`
    position: fixed;
    width: 100%; 
    height: 100%; 
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0,0,0,0.5); 
    z-index: 1; 


    display: flex;
    align-items:center;
    justify-content:center;

`

const AuthForm = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 400px;
    padding: 0 10px;
    background-color: #F6F3EF;

    border-style: solid;
    border-width: 2px;
    border-color: gray;
    box-shadow: 5px 10px 18px #888888;

    > * {
        margin-bottom: 25px;
    }
`

const Line = styled.div`
    height: 0;
    width: 80%;
    margin: 20px;
    border-bottom: 1px solid gray;
`
