import React, {useState, useEffect} from 'react';
import { getCourses } from '../components/utils/fetchs';
import ListSubheader from '@mui/material/ListSubheader';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';


const MyCourse = () => {

    const [courses, setCourses] = useState([])
    const [open, setOpen] = useState([]);

    const handleClick = (index) => {
        console.log(index)
        open[index]= !open[index]
        setOpen(...open);
    };
    useEffect(()=> {
        const fetchCourses = async() => {
            let response = await getCourses()
            console.log(response)
            setOpen(new Array(response.length).fill(false))
            setCourses(response)
        }
        fetchCourses()
    }, [])
    return ( 
        <>
            <List
                sx={{ width: '100%', maxWidth: 200, bgcolor: 'background.paper' }}
                component="nav"
                aria-labelledby="nested-list-subheader"
                subheader={
                    <ListSubheader component="div" id="nested-list-subheader">
                        Courses
                    </ListSubheader>
                }
                >
                {courses.map((course, index) => {
                    return (
                        <>
                            <ListItemButton key={index} onClick={()=>handleClick(index)}>
                                <ListItemText key={index} primary={course.title} />
                            </ListItemButton>
                            <Collapse key={index} in={open[index]} timeout="auto" unmountOnExit>
                                <List key={index} component="div" disablePadding>
                            {course.links.map((link, index) => {
                                return (        
                                    <ListItemButton key={index} sx={{ pl: 4 }}>
                                        <ListItemText key={index} primary={link.title} />
                                    </ListItemButton>   
                                )
                            })}
                                </List>
                            </Collapse>
                        </>
                    )
                })}
                <ListItemButton>
                    <ListItemText primary="Sent mail" />
                </ListItemButton>
                <ListItemButton>
                    <ListItemText primary="Drafts" />
                </ListItemButton>
                <Collapse in={open} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                    <ListItemButton sx={{ pl: 4 }}>
                        <ListItemText primary="Starred" />
                    </ListItemButton>
                    </List>
                </Collapse>
                <ListItemButton onClick={handleClick}>
                    <ListItemText primary="Inbox" />
                </ListItemButton>
                <Collapse in={open} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                    <ListItemButton sx={{ pl: 4 }}>
                        <ListItemText primary="Starred" />
                    </ListItemButton>
                    </List>
                </Collapse>
            </List>
        </>
    );
}

export default MyCourse;
