import React, {useState} from 'react';
import { deleteAccount, updateUsername } from '../components/utils/fetchs';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { saveUser, logOut } from '../components/utils/stores/user';
import { TextField, Button } from '@mui/material';
import styled from 'styled-components';

const Profile = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const [newUser, setNewUser ] = useState('')
    const onDeleteAccount = () => {
        let response = deleteAccount()
        dispatch(logOut())
        navigate('/')

    }

    const onUpdateAccount = async() => {
        let response = await updateUsername(newUser)
        console.log(response)
        dispatch(saveUser(response.username))
    }

    const currentUser={
        name: 'alejo',
        last_name: 'ortega',
        email: 'some@gmail.com',
        username: 'gosuto'
    }

    return ( 
    <Body>
        <h1>Change username</h1>
        <UserInfo>
            <h3>Name: {currentUser.name + ' ' + currentUser.last_name}</h3>
            <h3>Email: {currentUser.email}</h3>
            <TextField placeholder='type new username' value={newUser} onChange={e => setNewUser(e.target.value) }/>
            <Button onClick={onUpdateAccount}>update</Button>
            <Button onClick={onDeleteAccount}>Delete account</Button>
        </UserInfo>
    </Body> 
    );
}

export default Profile;

const Body = styled.div`
    height: 600px;
    display: flex;
    flex-direction: column;
    margin: 30px;
    > h1 {
        align-self: center;
    }
`
const UserInfo = styled.div`
    width: 100%
    > * {
        margin: 30px 0;
    }
`
