import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import TransferList from './TransferList';
import { getBannedUsers, getNotBannedUsers } from './utils/fetchs';

const Admin = () => {
    const [expanded, setExpanded] = useState(false);
    const [banned , setBanned] = useState([])
    const [notBanned, setNotBanned] = useState([])

    const handleChange = (panel) => (event, isExpanded) => {
      setExpanded(isExpanded ? panel : false);
    };

    useEffect(()=>{
      const getInfo = async() => {
        let response = await getBannedUsers()
        setBanned(...Object.values(response))
        response = await getNotBannedUsers()
        setNotBanned(...Object.values(response))
      }
      getInfo();
    }, [])

    return (
      <Body>
        <Accordion expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
          <AccordionSummary
            aria-controls="panel1bh-content"
            id="panel1bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>
              Users
            </Typography>
            <Typography sx={{ color: 'text.secondary' }}>Manage all the users</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <TransferList rightData={notBanned} leftData={banned}/>
          </AccordionDetails>
        </Accordion>


        <Accordion expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
          <AccordionSummary
            aria-controls="panel2bh-content"
            id="panel2bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>Courses</Typography>
            <Typography sx={{ color: 'text.secondary' }}>
              Manage all the courses availables
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>
              Donec placerat, lectus sed mattis semper, neque lectus feugiat lectus,
              varius pulvinar diam eros in elit. Pellentesque convallis laoreet
              laoreet.
            </Typography>
          </AccordionDetails>
        </Accordion>


        <Accordion expanded={expanded === 'panel3'} onChange={handleChange('panel3')}>
          <AccordionSummary
            aria-controls="panel3bh-content"
            id="panel3bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>
              Links
            </Typography>
            <Typography sx={{ color: 'text.secondary' }}>
              Create and Manage all the links to the courses
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>
              Nunc vitae orci ultricies, auctor nunc in, volutpat nisl. Integer sit
              amet egestas eros, vitae egestas augue. Duis vel est augue.
            </Typography>
          </AccordionDetails>
        </Accordion>
      </Body>
    );
  }

export default Admin;

const Body = styled.div`
    height: 500px;
`
