import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import TransferList from './TransferList';
import { getAdminCourses, getBannedUsers, getNotBannedUsers } from './utils/fetchs';
import List from './List';

const Admin = () => {
    const [expanded, setExpanded] = useState(false);
    const [banned , setBanned] = useState([])
    const [notBanned, setNotBanned] = useState([])
    const [courses, setCourses] = useState([])

    const coursesTitle = courses.map(course=> course.title);

    const handleChange = (panel) => (event, isExpanded) => {
      setExpanded(isExpanded ? panel : false);
    };

    useEffect(()=>{
      const getInfo = async() => {
        let response = await getBannedUsers()
        setBanned([...response.map(user => user.username)])
        response = await getNotBannedUsers()
        setNotBanned([...response.map(user => user.username)])
        response = await getAdminCourses()
        setCourses([...response])
      }
      getInfo();
    }, [])

    return (
      <Body>
        <Accordion expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
          <AccordionSummary
            aria-controls="panel1bh-content"
            id="panel1bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>
              Users
            </Typography>
            <Typography sx={{ color: 'text.secondary' }}>Manage all the users</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <TransferList rightData={notBanned} leftData={banned}/>
          </AccordionDetails>
        </Accordion>


        <Accordion expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
          <AccordionSummary
            aria-controls="panel2bh-content"
            id="panel2bh-header"
          >
            <Typography sx={{ width: '33%', flexShrink: 0 }}>Courses</Typography>
            <Typography sx={{ color: 'text.secondary' }}>
              Manage all the courses and link availables
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
              <List courses={coursesTitle}/>
          </AccordionDetails>
        </Accordion>

      </Body>
    );
  }

export default Admin;

const Body = styled.div`
    height: 500px;
`
